using Microsoft.CodeAnalysis.Text;
using Rocks.Extensions;
using Rocks.Models;
using System.CodeDom.Compiler;
using System.Text;

namespace Rocks.Builders.Create;

internal sealed class RockProjectionBuilder
{
	internal RockProjectionBuilder(ProjectedModelInformation projection)
	{
		this.ProjectedType = projection;
		(this.Name, this.Text) = this.Build();
	}

	private (string, SourceText) Build()
	{
		using var writer = new StringWriter();
		using var indentWriter = new IndentedTextWriter(writer, "\t");

		indentWriter.WriteLines(
			"""
			// <auto-generated/>

			#pragma warning disable CS8618
			#pragma warning disable CS8633
			#pragma warning disable CS8714
			#pragma warning disable CS8775

			#nullable enable

			""");

		var mockNamespace = this.ProjectedType.Type.Namespace;

		if (mockNamespace is not null)
		{
			indentWriter.WriteLines(
				$$"""
				namespace {{mockNamespace}}
				{				
				""");
			indentWriter.Indent++;
		}

		indentWriter.WriteLine("// TODO - insert projected code here...");

		if (mockNamespace is not null)
		{
			indentWriter.Indent--;
			indentWriter.WriteLine("}");
		}

		indentWriter.WriteLines(
			"""

			#pragma warning restore CS8618
			#pragma warning restore CS8633
			#pragma warning restore CS8714
			#pragma warning restore CS8775
			""");

		var text = SourceText.From(writer.ToString(), Encoding.UTF8);
		var name = $"{this.ProjectedType.Type.FullyQualifiedName.GenerateFileName()}_Projection.g.cs";
		return (name, text);
	}

	public string Name { get; private set; }
	public SourceText Text { get; private set; }
	private ProjectedModelInformation ProjectedType { get; }
}
